mapper.select:
SELECT * FROM ( SELECT ID_PROTOCOL_SIMULATION, M_SN, M_PRE, M_D_PRE, M_LEAKAGE, M_RESULT, OPER_TIME, DATE_FORMAT(OPER_TIME, '%Y-%m-%d') OPER_DATE, PROTOCOL_TYPE FROM app_protocol_simulation WHERE PROTOCOL_TYPE = #{protocolType, jdbcType=VARCHAR}
AND DATE_FORMAT(OPER_TIME, '%Y-%m-%d') = #{operDate, jdbcType=VARCHAR}
ORDER BY OPER_TIME DESC ) T LIMIT 11
